mapper.update:
UPDATE k_zan
SET status = #{status}
WHERE
	item_id = #{item_id}
AND user_id = #{user_id}

mapper.select:
select type_id, type_name, date_format(add_time,"%Y-%m-%d %T") as add_time, state from k_type where state=0
and type_id = #{type_id}
and type_name like concat('%', #{type_name},'%')

select count(1) as count from k_type where state=0
and type_id = #{type_id}
and type_name like concat('%', #{type_name},'%')

select know_id, type_id, title, content, date_format(add_time,"%Y-%m-%d %T") as add_time, author from k_know where state=0
and know_id = #{know_id}
and type_id = #{type_id}
and title like concat('%', #{title},'%')
and author like concat('%', #{author},'%')
and content like concat('%', #{content},'%')

select count(1) as count from k_know where state=0
and know_id = #{know_id}
and type_id = #{type_id}
and title like concat('%', #{title},'%')
and author like concat('%', #{author},'%')
and content like concat('%', #{content},'%')
